ra.pdf\\" target=\\"_blank\\" rel=\\"noopener\\">pdf<\\/a><\\/p>"},"title":{"en_US":"Copyright Notice","pl_PL":"Prawa autorskie"}},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},"children":[],"_data":{"id":"573","menuId":"56","menuItemId":"573","parentId":"472","seq":"23"},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false}],"_data":{"id":"570","menuId":"56","menuItemId":"472","parentId":"0","seq":"20"},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},{"navigationMenuItem":{"navigationMenuItems":[],"_isDisplayed":true,"_isChildVisible":false,"_data":{"id":"467","contextId":"5","type":"NMI_TYPE_CURRENT","titleLocaleKey":"navigation.current"},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},"children":[],"_data":{"id":"574","menuId":"56","menuItemId":"467","parentId":"0","seq":"24"},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},{"navigationMenuItem":{"navigationMenuItems":[],"_isDisplayed":true,"_isChildVisible":false,"_data":{"id":"468","contextId":"5","type":"NMI_TYPE_ARCHIVES","titleLocaleKey":"navigation.archives"},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},"children":[],"_data":{"id":"575","menuId":"56","menuItemId":"468","parentId":"0","seq":"25"},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdapt